import React, { useState } from "react";

import "./ExpensesFilter.css";

const ExpensesFilter = (props) => {

    const [yearEntered, setYearEntered] = useState("2021");

    const yearChangedHandler = (event) => {
        setYearEntered(event.target.value);
    };

    props.onExpenseFilter(yearEntered);

  return (
    <div className="expenses-filter">

      <div className="expenses-filter__control">

        <label>Filter by year</label>
        <select>
          <option value="2022" onChange={yearChangedHandler} >2022</option>
          <option value="2021" onchange={yearChangedHandler} >2021</option>
          <option value="2020" onchange={yearChangedHandler} >2020</option>
          <option value="2019" onchange={yearChangedHandler} >2019</option>
        </select>

      </div>

    </div>
  );
};

export default ExpensesFilter;
