import React from "react"; // dont need this tho    
import ExpenseForm from "./ExpenseForm";
import "./NewExpense.css";


// This component will return an HTML form for USER inputs, where they can add their expenses.
const NewExpense = () => {
    const saveExpenseDataHandler = (enteredExpenseData) => { // We get the expense data properties through this parameter. Check "ExpenseForm.js" for explanation.
        const expenseData = {
            ...enteredExpenseData,
            id: Math.random().toString,
        }
        console.log(expenseData);
    };


    return (
        <div className="new-expense">
            <ExpenseForm onSaveExpenseData={saveExpenseDataHandler} /> {/* passes the "saveExpenseDataHandler" function to the "ExpenseForm.js" component, 
                                                                            through this parameter. by using it like this in the "ExpenseForm.js" component: 
                                                                                                        "props.onSave*/}
        </div>
    )
};

export default NewExpense;
