import React, { useState } from 'react';
import './ExpenseForm.css';

// Event listeners (listens to an event) in React:
    // "onInput" ---> listens/triggers on every KEYSTROKE, can only be used for 
    // "onChange" ---> also listens/triggers on every KEYSTROKE, but the advantage of it is that it can be used for every type of input 
    //                 (for example: also for drop menus)


const ExpenseForm = () => {

    const [titleEntered, setTitleEntered] = useState(""); // the "useState" parameter is an EMPTY STRING, because initially the value of the title input (that 
    //                                                       we want to store) .

    const titleChangeHandler = (event) => { // the parameter within the Callback function (event) refers to what triggered the event
        console.log(event.target.value); // console logs the KEYSTROKE that caused the event
    };

    // After we get that key value of the KEY PRESSED --> we want to store it somewhere inorder to use that value.
    // Inother words, we want to combine all the values, for ALL the inputs, and then combine them as an object when the form is submitted.

    // Inorder to store these values, and make sure that it survives if the this component/function is re-executed --> WE USE THE "useState" HOOK.

    return (
        <form>
            <div className='expense-form'>
                <div className='expense-form__control'>
                    <label>Title</label>
                    <input type='text' onChange={titleChangeHandler} />
                </div>

                <div className='expense-form__control'>
                    <label>Amount</label>
                    <input type='number' min='0.01' step='0.01' />
                </div>
            
                <div className='expense-form__control'>
                    <label>Date</label>
                    <input type='date' min='2019-01-01' max='2022-12-31' />
                </div>
            </div>          

            <div className='expense-form__actions'>
                <button type='submit'>Add Expense</button>
            </div>

        </form>
    )
};


export default ExpenseForm;
